import React, { Fragment, useState } from 'react';

function EditRestaurant({ restaurant }) {
	const [restaurantName, setRestaurantName] = useState(
		restaurant.restaurant_name
	);
	const [location, setLocation] = useState(restaurant.location);
	const [priceRange, setPriceRange] = useState(restaurant.price_range);

	const deleteUnsavedChanges = (restaurant) => {
		setName(restaurant.restaurant_name);
		setLocation(restaurant.location);
		setPriceRange(restaurant.price_range);
	};

	const updateDescription = async (e) => {
		// e.preventDefault();
		// try {
		// 	const body = { description };
		// 	const response = await fetch(
		// 		`http://localhost:5000/todos/${todo.todo_id}`,
		// 		{
		// 			method: 'PUT',
		// 			headers: { 'Content-Type': 'application/json' },
		// 			body: JSON.stringify(body),
		// 		}
		// 	);

		// 	window.location = '/';
		// } catch (error) {
		// 	console.log(error.message);
		// }
		console.log(e);
	};

	return (
		<Fragment>
			<button
				type="button"
				className="btn btn-info"
				data-toggle="modal"
				data-target={`#id${restaurant.restaurant_id}`}
			>
				Edit
			</button>

			<div
				className="modal"
				id={`id${restaurant.restaurant_id}`}
				onClick={() => setDescription(restaurant.restaurant_name)}
			>
				<div className="modal-dialog">
					<div className="modal-content">
						<div className="modal-header">
							<h4 className="modal-title">Edit Restaurant</h4>

							{/* top right cross that closes the modal */}
							<button
								type="button"
								className="close"
								data-dismiss="modal"
								onClick={() => deleteUnsavedChanges()}
							>
								&times;
							</button>
						</div>

						<div className="modal-body">
							<input
								type="text"
								className="form-control"
								value={restaurantName}
								onChange={(e) => setDescription(e.target.value)}
							/>
							<input
								type="text"
								className="form-control"
								value={location}
								onChange={(e) => setLocation(e.target.value)}
							/>
							<input
								type="text"
								className="form-control"
								value={priceRange}
								onChange={(e) => setPriceRange(e.target.value)}
							/>
						</div>

						<div className="modal-footer">
							<button
								type="button"
								className="btn btn-danger"
								data-dismiss="modal"
								onClick={() => deleteUnsavedChanges()}
							>
								Close
							</button>
							<button
								type="button"
								className="btn btn-info"
								data-dismiss="modal"
								onClick={(e) => updateDescription(e)}
							>
								Edit
							</button>
						</div>
					</div>
				</div>
			</div>
		</Fragment>
	);
}

export default EditRestaurant;
