import React, { useState, useEffect, useContext } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import TableSortLabel from '@mui/material/TableSortLabel';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import RestaurantFinder from '../api/RestaurantFinder';
import { RestaurantsContext } from '../context/ContextRestaurant';

function createData(restaurantName, restaurantLocation, priceRange, ratings) {
	return { restaurantName, restaurantLocation, priceRange, ratings };
}

const rows = [
	createData(1, 'Apple', 5, 3),
	createData(2, 'Orange', 2, 2),
	createData(3, 'Grapes', 3, 1),
	createData(4, 'Tomato', 2, 1.6),
	createData(5, 'Mango', 1.5, 4),
];

const SortedTable = (props) => {
	useEffect(() => {
		async function fetchData() {
			try {
				const { restaurants, setRestaurants } = useContext(RestaurantsContext);
				const response = await RestaurantFinder.get('/restaurants');
				console.log(response.data.data);
				setRestaurants(response.data);
				console.log(restaurants);
			} catch (err) {
				console.log(err.message);
			}
		}

		fetchData();
	}, []);

	const [rowData, setRowData] = useState(rows);
	const [orderDirection, setOrderDirection] = useState('asc');

	const sortArray = (arr, orderBy) => {
		switch (orderBy) {
			case 'asc':
			default:
				return arr.sort((a, b) =>
					a.ratings > b.ratings ? 1 : b.ratings > a.ratings ? -1 : 0
				);
			case 'desc':
				return arr.sort((a, b) =>
					a.ratings < b.ratings ? 1 : b.ratings < a.ratings ? -1 : 0
				);
		}
	};

	const handleSortRequest = () => {
		setRowData(sortArray(rows, orderDirection));
		setOrderDirection(orderDirection === 'asc' ? 'desc' : 'asc');
	};

	return (
		<TableContainer component={Paper}>
			<Table aria-label="simple table">
				<TableHead>
					<TableRow>
						<TableCell align="center">Restaurant Name</TableCell>

						<TableCell align="center">Location</TableCell>

						<TableCell align="center">Price Range</TableCell>

						<TableCell align="center" onClick={handleSortRequest}>
							<TableSortLabel active={true} direction={orderDirection}>
								Ratings
							</TableSortLabel>
						</TableCell>
						<TableCell align="right">Edit</TableCell>
						<TableCell align="right">Delete</TableCell>
					</TableRow>
				</TableHead>
				<TableBody>
					{rowData.map((row) => (
						<TableRow key={row.restaurantName}>
							<TableCell component="th" scope="row" align="center">
								{row.restaurantName}
							</TableCell>
							<TableCell align="center">{row.restaurantLocation}</TableCell>
							<TableCell align="center">{row.priceRange}</TableCell>
							<TableCell align="center">{row.ratings}</TableCell>
							<TableCell align="right">
								<Button variant="contained" color={'primary'}>
									Edit
								</Button>
							</TableCell>
							<TableCell align="right">
								<Button variant="contained" color={'primary'}>
									Delete
								</Button>
							</TableCell>
						</TableRow>
					))}
				</TableBody>
			</Table>
		</TableContainer>
	);
};

export default SortedTable;
