import React, { Fragment, useState } from 'react';

function EditRestaurant({ restaurant }) {
	const [restaurantName, setRestaurantName] = useState(
		restaurant.restaurantName
	);
	const [location, setLocation] = useState(restaurant.location);
	const [description, setDescription] = useState(restaurant.description);

	const updateDescription = async (e) => {
		e.preventDefault();
		try {
			const body = { description };
			const response = await fetch(
				`http://localhost:5000/restaurants/${restaurant.restaurant_id}`,
				{
					method: 'PUT',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify(body),
				}
			);

			window.location = '/';
		} catch (error) {
			console.log(error.message);
		}
	};

	return (
		<Fragment>
			<button
				type="button"
				className="btn btn-info"
				data-toggle="modal"
				data-target={`#id${restaurant.restaurant_id}`}
			>
				Edit
			</button>

			<div
				className="modal"
				id={`id${restaurant.restaurant_id}`}
				onClick={() => setDescription(restaurant.restaurant_name)}
			>
				<div className="modal-dialog">
					<div className="modal-content">
						<div className="modal-header">
							<h4 className="modal-title">Edit to do item</h4>
							<button
								type="button"
								className="close"
								data-dismiss="modal"
								onClick={() => setDescription(restaurant.description)}
							>
								&times;
							</button>
						</div>

						<div className="modal-body">
							<input
								type="text"
								className="form-control"
								value={description}
								onChange={(e) => setDescription(e.target.value)}
							/>
						</div>

						<div className="modal-footer">
							<button
								type="button"
								className="btn btn-danger"
								data-dismiss="modal"
								onClick={() => setDescription(restaurant.description)}
							>
								Close
							</button>
							<button
								type="button"
								className="btn btn-info"
								data-dismiss="modal"
								onClick={(e) => updateDescription(e)}
							>
								Edit
							</button>
						</div>
					</div>
				</div>
			</div>
		</Fragment>
	);
}

export default EditRestaurant;
