import React, { Fragment, useState } from 'react';
import RestaurantFinder from '../api/RestaurantFinder';

function EditRestaurant({ restaurant }) {
	const [name, setName] = useState(restaurant.name);
	const [location, setLocation] = useState(restaurant.location);
	const [priceRange, setPriceRange] = useState(restaurant.priceRange);

	const deleteUnsavedChanges = (restaurant) => {
		setName(restaurant.name);
		setLocation(restaurant.location);
		setPriceRange(restaurant.priceRange);
	};

	// const [description, setDescription] = useState(todo.description);

	const updateRestaurant = async (e, id) => {
		e.preventDefault();
		try {
			const body = { restaurant_name: name, location, price_range: priceRange };
			const response = await RestaurantFinder.put(`/restaurants/${id}`, body);

			window.location = '/';
		} catch (error) {
			console.log(error.message);
		}
	};

	return (
		<Fragment>
			<button
				type="button"
				className="btn btn-info"
				data-toggle="modal"
				data-target={`#id${restaurant.restaurant_id}`}
			>
				Edit
			</button>

			<div
				className="modal"
				id={`id${restaurant.restaurant_id}`}
				// onClick={() => setDescription(restaurant.description)}
			>
				<div className="modal-dialog">
					<div className="modal-content">
						<div className="modal-header">
							<h4 className="modal-title">Edit to do item</h4>
							<button
								type="button"
								className="close"
								data-dismiss="modal"
								onClick={() => deleteUnsavedChanges(restaurant)}
							>
								&times;
							</button>
						</div>

						<div className="modal-body">
							<input
								type="text"
								className="form-control"
								value={'description'}
								onChange={(e) => deleteUnsavedChanges(e.target.value)}
							/>
						</div>

						<div className="modal-footer">
							<button
								type="button"
								className="btn btn-danger"
								data-dismiss="modal"
								onClick={() => deleteUnsavedChanges(restaurant.restaurant_name)}
							>
								Close
							</button>
							<button
								type="button"
								className="btn btn-info"
								data-dismiss="modal"
								onClick={(e) => updateRestaurant(e, restaurant.restaurant_id)}
							>
								Edit
							</button>
						</div>
					</div>
				</div>
			</div>
		</Fragment>
	);
}

export default EditRestaurant;
