import { Button } from '@mui/material';
import React, { Fragment, useState } from 'react';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import MenuItem from '@mui/material/MenuItem';

function EditRestaurant({ restaurant }) {
	console.log(restaurant);

	const howExpensive = [
		{
			value: '0',
			label: 'Not Defined',
		},
		{
			value: '1',
			label: 'Cheap',
		},
		{
			value: '2',
			label: 'Economic',
		},
		{
			value: '3',
			label: 'Medium',
		},
		{
			value: '4',
			label: 'Someplace nice',
		},
		{
			value: '5',
			label: 'Expensive AF',
		},
	];

	const [restaurantName, setRestaurantName] = useState(
		restaurant.restaurant_name
	);
	const [location, setLocation] = useState(restaurant.location);
	const [priceRange, setPriceRange] = useState(restaurant.price_range);

	const deleteUnsavedChanges = (restaurant) => {
		setRestaurantName(restaurant.restaurant_name);
		setLocation(restaurant.location);
		setPriceRange(restaurant.price_range);
	};

	const handleSubmit = async (event) => {
		event.preventDefault();
		try {
			const response = await RestaurantFinder.post('/restaurants', {
				restaurant_name: name,
				location,
				price_range: priceRange,
			});
			addRestaurant(response.data.data[0]);
			console.log(response);
		} catch (error) {
			console.log(error.message);
		}
	};

	return (
		<Fragment>
			<button
				type="button"
				className="btn btn-primary"
				data-bs-toggle="modal"
				data-bs-target={`#id${restaurant.restaurant_id}`}
			>
				Edit
			</button>

			<div
				className="modal fade"
				id={`id${restaurant.restaurant_id}`}
				tabindex="-1"
				aria-labelledby="exampleModalLabel"
				aria-hidden="true"
			>
				<div className="modal-dialog">
					<div className="modal-content">
						<div className="modal-header">
							<h5 className="modal-title" id="exampleModalLabel">
								Edit Restaurant
							</h5>
							<button
								type="button"
								className="btn-close"
								data-bs-dismiss="modal"
								aria-label="Close"
								onClick={() => deleteUnsavedChanges()}
							></button>
						</div>
						<div className="modal-body">
							<Box
								component="form"
								sx={{
									'& > :not(style)': { m: 1, width: '25ch' },
								}}
								noValidate
								autoComplete="off"
							>
								<TextField
									id="outlined-basic"
									label="Restaurant Name"
									variant="outlined"
									value={restaurantName}
									onChange={(e) => setRestaurantName(e.target.value)}
								/>
								<TextField
									id="outlined-basic"
									label="Location"
									variant="outlined"
									value={location}
									onChange={(e) => setLocation(e.target.value)}
								/>
								<TextField
									id="outlined-select-currency"
									select
									label="Price range"
									value={priceRange}
									onChange={(e) => setPriceRange(e.target.value)}
								>
									{howExpensive.map((option) => (
										<MenuItem key={option.value} value={option.value}>
											{option.label}
										</MenuItem>
									))}
								</TextField>
							</Box>
						</div>
						<div className="modal-footer">
							<button
								type="button"
								className="btn btn-secondary"
								data-bs-dismiss="modal"
							>
								Close
							</button>
							<button type="button" className="btn btn-primary">
								Save changes
							</button>
						</div>
					</div>
				</div>
			</div>
		</Fragment>
	);
}

export default EditRestaurant;
